settings.liquid:
{% assign lieferumfang = product.metafields.custom.lieferumfang %}

{% if lieferumfang != null and lieferumfang %}
  <div class="lieferumfang-container">
    <span class="lieferumfang">Uhr mit Box und Papieren, Internationale Garantiekarte sowie Herstellerverpackung</span>
  </div>

  <style>
    .lieferumfang-container {
      text-align: center;
      margin-top: -10px;
      margin-bottom: 10px;
    }

    .lieferumfang {
      color: #000000;
      font-size: 12px;
    }
  </style>
{% endif %}

{% unless product.tags contains 'Uhren-Archiv' %}
  <center style="margin-top: -25px; margin-bottom: 20px; font-size: 12px;">
    {% if request.locale.iso_code == "de" %}
      <span style="color: #8f8f8f;">Preise inkl. MwSt.</span> 
      <span style="color: #c6ae8e; text-decoration: underline;"><a href="https://www.google.de/" style="color: #c6ae8e;">zzgl. Versandkosten</a></span>
    {% else %}
      <span style="color: #8f8f8f;">Prices incl. VAT.</span> 
      <span style="color: #c6ae8e; text-decoration: underline;"><a href="https://www.google.com/" style="color: #c6ae8e;">plus shipping costs</a></span>
    {% endif %}
  </center>
{% endunless %}

settings.custom_liquid:
{% if request.locale.iso_code == "de" %}
<style>
.features-container {
  display: flex;
  flex-wrap: wrap;
  background-color: #f7f7f7;
  border-radius: 20px;
  padding: 20px;
  gap: 10px; /* Abstand zwischen den Elementen */
}

.feature-item {
  display: flex;
  align-items: center;
  gap: 10px; /* Abstand zwischen Icon und Text */
  flex: 0 1 auto; /* Changed to allow for text wrapping */
  padding: 20px;
}

.feature-icon {
  width: 24px;
  height: 24px;
  fill: currentColor; /* Farbe des Icons */
}

/* Media Queries für Responsivität */
@media (max-width: 768px) {
  .feature-item {
    flex-basis: calc(50% - 20px); /* Change to allow two items per row with wrapping text */
  }
}

@media (max-width: 480px) {
  .feature-item {
    flex-basis: 100%; /* Ein Element pro Zeile */
  }
}
</style>

